import React from 'react';
import './exerciseDisplay.scss';
import axios from 'axios';
import { Button, TextField } from '@material-ui/core';

function ExerciseDisplay(props) {


    return (
        <>
            {props.exercises.map((exercise) => {
                console.log(exercise.id)
                deleteExercise = (exercise) => {
                    axios.delete(`http://localhost:8080/deleteExercise/${exercise.id}`).then(
                        window.location.reload()
                    );
                }
                return (
                    <>
                        <div className="exerciseDisplay">
                            <div className="exerciseDisplay__item-row exerciseDisplay__item-row--margin">
                                <TextField
                                    className="exerciseDisplay__item-row-single"
                                    id="filled-basic"
                                    label="Exercise"
                                    variant="filled"
                                    defaultValue={exercise.exerciseName}
                                />
                                <TextField
                                    className="exerciseDisplay__item-row-single"
                                    id="filled-basic"
                                    label="Muscle"
                                    variant="filled"
                                    defaultValue={exercise.muscle}
                                />
                            </div>
                            <div className="exerciseDisplay__item-row">
                                <TextField
                                    className="exerciseDisplay__item-row-single"
                                    id="filled-basic"
                                    label="Reps/Time"
                                    variant="filled"
                                    defaultValue={exercise.repsTime}

                                />
                                <TextField
                                    className="exerciseDisplay__item-row-single"
                                    id="filled-basic"
                                    label="Weight"
                                    variant="filled"
                                    defaultValue={exercise.weight}
                                />
                            </div>

                            <div className="exerciseDisplay__btn">
                                <Button
                                    variant="contained"
                                    className="exerciseDisplay__btn-single"
                                    
                                    >
                                    Delete
                                </Button>
                            </div>
                        </div>
                    </>
                )
            })
            }
        </>
    )
};

export default ExerciseDisplay;
