import React from 'react';
import './exerciseComponent.scss';
import axios from 'axios'
import { TextField } from '@material-ui/core';

class ExerciseComponent extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            exercise: this.props.exercise
        }
    }

    onChange = (e, id) => {
