import React from 'react';
import './createWorkout.scss'
import axios from 'axios'
import { FormControl, TextField } from '@material-ui/core';
import ExerciseComponent from '../ExerciseComponent/ExerciseComponent.jsx'
import Footer from '../Footer/Footer.jsx'
import Header from '../Header/Header'
import { Button } from '@material-ui/core';


function CreateWorkout(props) {

    function createWorkouts(props) {
        axios.post(`http://localhost:8080/createWorkout`, {
            workoutName: props.workoutName,

        }).then((response) => {
            axios.get('http://localhost:8080/createWorkout').then((response) => {
                console.log('client get request');
                console.log(response.data);
                //   this.setState({
                //     workouts: response.data,
                // updated: true,
                //   });
            });
        });
    };

    function addExerciseToWorkout (props) {
        const targetWorkout = props.workouts.slice(-1);
        console.log(targetWorkout);
        // console.log(props.workouts.length -1.id);
        // console.log(props.exercises);

        //target workout ID
        //target exercise ID
        //on Add = update workoutExerciseData workoutID w/ exerciseId
    }

    return props.exercises && props.workouts && (
        <>
            <Header />
            <div className="createWorkout">
                <div className="createWorkout__input">
                    <div className="createWorkout__input-workout--margin">
                        {/* {console.log(props)} */}
                        <TextField
                            className="createWorkout__input-workout"
                            id="filled-basic"
                            label="Name Workout"
                            variant="filled"
                            name="workoutName"
                            defaultValue=""
                            onChange={props.onChange}
                            onBlur={(e) => {
                                e.preventDefault();
                                createWorkouts(props)
                            }}
                        />
                    </div>

                    <FormControl variant="filled" className="createWorkout__input-exercise">
                        <TextField
                            id="filled-select-currency-native"
                            select
                            label="Select Exercise"
                            // onChange={handleChange}
                            variant="filled" >
                            {props.exercises.map((props) => (
                                <option value={props.exerciseName}>
                                    {props.exerciseName}
                                </option>
                            ))}
                        </TextField>
                    </FormControl>
                </div>

                <div className="createWorkout__btn--divider">
                    <Button className="createWorkout__btn-link"
                        onClick={ () => {addExerciseToWorkout(props)}}
                        variant="contained"
                        color="primary"
                        className="createWorkout__btn--size">
                        Add Exercise
                </Button>
                </div>

                {props.exercises.map((exercise) => {
                    return (
                        <div className="createWorkout__item">
                            <ExerciseComponent exercise={exercise} updateExercise={props.updateExercise} />
                        </div>
                    )
                })}

                <Footer />
            </div>
        </>
    );
}

export default CreateWorkout;
