import React from 'react';
import './createExercise.scss'
import Header from '../Header/Header'
import ExerciseComponent from '../ExerciseComponent/ExerciseComponent'
import DeleteBtn from '../DeleteBtn/DeleteBtn'
import Footer from '../Footer/Footer.jsx'
import TextField from '@material-ui/core/TextField';
import axios from 'axios'
import { Button } from '@material-ui/core';

function CreateExercise(props) {

    function createExercise () {
        
        axios.post(`http://localhost:8080/createExercise`, {
          exerciseName: this.state.exerciseName,
          muscle: this.state.muscle,
          repsTime: this.state.repsTime,
          weight: this.state.weight
        }).then((response) => {
          axios.get('http://localhost:8080/createExercise').then((response) => {
    
            this.setState({
              exercises: response.data,
              updated: true,
            });
          });
        });
      };

    return (
        <>
            <Header />

            <div className="createExercise">
                <div className="createExercise__field">
                    <h3 className="createExercise__field-name">Exercise</h3>
                    <TextField
                        className="createExercise__field-input"
                        id="filled-basic"
                        label="Enter"
                        variant="filled"
                        name="exerciseName"
                        onChange={props.onChange}
                    />
                </div>
                <div className="createExercise__field">
                    <h3 className="createExercise__field-name">Muscle</h3>
                    <TextField
                        className="createExercise__field-input"
                        id="filled-basic"
                        label="Enter"
                        variant="filled"
                        name="muscle"
                        onChange={props.onChange}
                    />
                </div>
                <div className="createExercise__field">
                    <h3 className="createExercise__field-name">Reps/Time</h3>
                    <TextField
                        className="createExercise__field-input"
                        id="filled-basic"
                        label="Enter"
                        variant="filled"
                        name="repsTime"
                        onChange={props.onChange}
                    />
                </div>
                <div className="createExercise__field createExercise__field--divider">
                    <h3 className="createExercise__field-name">Weight</h3>
                    <TextField
                        className="createExercise__field-input"
                        id="filled-basic"
                        label="Enter"
                        variant="filled"
                        name="weight"
                        onChange={props.onChange}
                    />
                </div>

                <Button className="footer__btn-link"
                    onClick={createExercise}
                    variant="contained"
                    color="primary"
                    className="footer__btn--size">
                    Create Exercise
                </Button>


                {props.exercises && props.exercises.map((exercise) => {
                    return (
                        <>
                            <ExerciseComponent exercise={exercise} updateExercise={props.updateExercise} />
                            <DeleteBtn exercise={exercise} />
                        </>
                    )
                })
                };

                <Footer exercises={props.exercises} addExercise={props.addExercise} createExercise={props.createExercise} />
            </div>
        </>
    );
}

export default CreateExercise;
