import React from 'react';
import './exerciseComponent.scss';
import axios from 'axios'
import { TextField } from '@material-ui/core';

class ExerciseComponent extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            exercise: this.props.exercise
        }
    }

    updateExercise = (e, id) => {
        e.preventDefault();
        console.log("PR button clicked");
        {console.log(this.props.exercise);}
    
        axios.put(`http://localhost:8080/updateExercise/${id}`, {
    
          exerciseName: this.state.exercises.exerciseName,
          muscle: this.state.muscle,
          repsTime: this.state.repsTime,
          weight: this.state.weight
        
        }).then((response) => {
          axios.get('http://localhost:8080/createExercise').then((response) => {
            
            console.log(response.data);
            this.setState({
              exercises: response.data,
            });
          })
        })
      }

    render() {
        return (
            <div className="exerciseComponent">
                <div className="exerciseComponent__item-row exerciseComponent__item-row--margin">
                    <TextField
                        className="exerciseComponent__item-row-single"
                        id="filled-basic"
                        label="Exercise"
                        variant="filled"
                        name="exerciseName"
                        defaultValue={this.props.exercise.exerciseName}
                        onBlur={(e) => {
                            e.preventDefault();
                            this.props.updateExercise(e, this.state.exercise.id)
                        }}
                    />
                    <TextField
                        className="exerciseComponent__item-row-single"
                        id="filled-basic"
                        label="Muscle"
                        variant="filled"
                        defaultValue={this.props.exercise.muscle}
                    />
                </div>
                <div className="exerciseComponent__item-row">
                    <TextField
                        className="exerciseComponent__item-row-single"
                        id="filled-basic"
                        label="Reps/Time"
                        variant="filled"
                        defaultValue={this.props.exercise.repsTime}
                    />
                    <TextField
                        className="exerciseComponent__item-row-single"
                        id="filled-basic"
                        label="Weight"
                        variant="filled"
                        defaultValue={this.props.exercise.weight}
                    />
                </div>
            </div>
        )
    }
};

export default ExerciseComponent;
