import React from 'react';
import './createWorkout.scss'
import axios from 'axios'
import {FormControl, TextField} from '@material-ui/core';
import ExerciseComponent from '../ExerciseComponent/ExerciseComponent.jsx'
import Footer from '../Footer/Footer.jsx'
import Header from '../Header/Header'

function CreateWorkout(props) {

    createWorkoutName = (e, id) => {
        const newWorkout = this.state.workouts.map((workoutItem) => {
          if (workoutItem.id === id) {
            let updatedWorkoutItem = {
              ...workoutItem
            }
            updatedWorkoutItem[e.target.name] = e.target.value
            return updatedWorkoutItem;
          };
          return workoutItem;
        })
        console.log(newWorkout);
        this.setState({ exercises: newWorkout })
        let ui = this.state.workouts.find((wo) => wo.id === id)
    
        axios.post(`http://localhost:8080/createWorkout/${ui.id}`, {
          workoutname: ui.exerciseName,
        })
      };

    return props.exercises && props.workouts && (
        <>
            <Header />
            <div className="createWorkout">
                <div className="createWorkout__input">
                    <div className="createWorkout__input-workout--margin">
                        <TextField
                            className="createWorkout__input-workout"
                            id="filled-basic"
                            label="Name Workout"
                            variant="filled"
                            name="workoutName"
                            onChange={props.onChange}
                            onBlur={(e) => {
                                e.preventDefault();
                                createWorkoutName(props.workouts)
                            }}
                        />
                    </div>

                    <FormControl variant="filled" className="createWorkout__input-exercise">
                    {console.log(props)}
                        <TextField
                            id="filled-select-currency-native"
                            select
                            label="Select Exercise"
                            // onChange={handleChange}
                            variant="filled" >
                            {props.exercises.map((props) => (
                                <option value={props.exerciseName}>
                                    {props.exerciseName}
                                </option>
                            ))}
                        </TextField>
                    </FormControl>
                </div>

                {props.exercises.map((exercise) => {
                    return (
                        <div className="selectWorkout__item">
                            <ExerciseComponent exercise={exercise} updateExercise={props.updateExercise}/>
                        </div>
                    )
                })}

                <Footer />
            </div>
        </>
    );
}

export default CreateWorkout;
